mapper.delete:
delete from applicant_tbl
where matching_room_code = ${param1}
and   usercode           = ${param2}

mapper.update:
update applicant_tbl set accept_yn='Y'
where  matching_room_code=${param1}
and    usercode=${param2}

update applicant_tbl set accept_yn='N'
where  matching_room_code=${param1}
and    usercode=${param2}
