mapper.select:
SELECT cart_code FROM (
SELECT #{cartCode} AS cart_code
) AS codes WHERE cart_code NOT IN ( SELECT cart_code FROM order_tbl )

mapper.update:
UPDATE order_tbl
SET total_amount = (#{price} * #{quantity}),
quantity = #{quantity},
price = #{price},
marathon_code = #{marathon_code}
WHERE cart_code = #{cart_code}
AND usercode = #{usercode}
